import React from 'react';
import PropTypes from 'prop-types';

const UserModal = ({ isOpen, formData, setFormData, onClose, onSubmit, isEditMode, validationErrors }) => {
    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded shadow-lg w-[90%] sm:w-[500px]">
                <div className="flex justify-between items-center mb-4">
                    <h3 className="text-xl font-semibold">{isEditMode ? 'Edit User' : 'Create New User'}</h3>
                    <button onClick={onClose} className="text-gray-600 text-xl">&times;</button>
                </div>
                <form onSubmit={onSubmit} className="space-y-4">
                    {['first_name', 'last_name', 'email', 'profile_link_image'].map(field => (
                        <div key={field}>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                <span className="text-red-500">*</span>
                                {field.replace('_', ' ').replace(/\b\w/g, c => c.toUpperCase())}
                            </label>
                            <input
                                type={field === 'email' ? 'email' : 'text'}
                                name={field}
                                value={formData[field]}
                                onChange={e => setFormData({ ...formData, [field]: e.target.value })}
                                placeholder={`Please enter ${field.replace('_', ' ')}`}
                                className="w-full border p-2 rounded"
                            />
                            {validationErrors[field] && (
                                <p className="text-red-500 text-sm mt-1">{validationErrors[field]}</p>
                            )}
                        </div>
                    ))}
                    <div className="flex justify-end mt-6 space-x-2">
                        <button type="button" onClick={onClose} className="border px-4 py-2 rounded">Cancel</button>
                        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
                            {isEditMode ? 'Update' : 'Submit'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

UserModal.propTypes = {
    isOpen: PropTypes.bool.isRequired,
    onClose: PropTypes.func.isRequired,
    onSubmit: PropTypes.func.isRequired,
    formData: PropTypes.object.isRequired,
    setFormData: PropTypes.func.isRequired,
    isEditMode: PropTypes.bool.isRequired,
    validationErrors: PropTypes.object.isRequired,
};

export default UserModal;
