import React from 'react';
import PropTypes from 'prop-types';

const UserCardGrid = ({ users, onEdit, onDelete }) => {
    return (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 mt-4">
            {users.map(user => (
                <div key={user.id} className="bg-white shadow-md rounded p-4 flex flex-col items-center">
                    <img
                        src={user.avatar || user.profile_link_image}
                        alt={`${user.first_name} ${user.last_name}`}
                        className="w-20 h-20 rounded-full mb-4"
                    />
                    <h3 className="text-lg font-semibold mb-1">{user.first_name} {user.last_name}</h3>
                    <p className="text-gray-600 mb-4">{user.email}</p>
                    <div className="flex space-x-2">
                        <button
                            onClick={() => onEdit(user)}
                            className="bg-yellow-400 hover:bg-yellow-500 text-white px-3 py-1 rounded"
                        >
                            Edit
                        </button>
                        <button
                            onClick={() => onDelete(user.id)}
                            className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded"
                        >
                            Delete
                        </button>
                    </div>
                </div>
            ))}
        </div>
    );
};

UserCardGrid.propTypes = {
    users: PropTypes.array.isRequired,
    onEdit: PropTypes.func.isRequired,
    onDelete: PropTypes.func.isRequired,
};

export default UserCardGrid;
