import React from 'react';
import PropTypes from 'prop-types';

const UserTable = ({ users, onEdit, onDelete, loading }) => {
    if (loading) return <p>Loading users...</p>;

    return (
        <table className="w-full border rounded overflow-hidden">
            <thead className="bg-gray-200">
                <tr>
                    <th className="p-3">ID</th>
                    <th className="p-3">Name</th>
                    <th className="p-3">Email</th>
                    <th className="p-3">Actions</th>
                </tr>
            </thead>
            <tbody>
                {users?.length === 0 && (
                    <tr>
                        <td colSpan="4" className="text-center py-4 text-gray-500">
                            No users found
                        </td>
                    </tr>
                )}
                {users?.map(user => (
                    <tr key={user.id} className="border-t">
                        <td className="p-3">{user.id}</td>
                        <td className="p-3">{user.first_name} {user.last_name}</td>
                        <td className="p-3">{user.email}</td>
                        <td className="p-3 space-x-2">
                            <button onClick={() => onEdit(user)} className="px-3 py-1 bg-yellow-500 text-white rounded">Edit</button>
                            <button onClick={() => onDelete(user)} className="px-3 py-1 bg-red-600 text-white rounded">Delete</button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

UserTable.propTypes = {
    users: PropTypes.arrayOf(PropTypes.object).isRequired,
    onEdit: PropTypes.func.isRequired,
    onDelete: PropTypes.func.isRequired,
    loading: PropTypes.bool.isRequired,
};

export default UserTable;
