import React, { useState } from 'react';
import { useUsers } from '../hooks/useUsers';
import { validateUser } from '../utils/validators';
import UserTable from '../components/UserTable';
import UserCardGrid from '../components/UserCardGrid';
import UserModal from '../components/UserModal';
import ConfirmModal from '../components/ConfirmModal';

const Users = () => {
    const { users, loading, saveUser, deleteUser } = useUsers();

    const [viewType, setViewType] = useState('table'); // 'table' or 'card'
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalMode, setModalMode] = useState('create'); // 'create' or 'edit'
    const [selectedUser, setSelectedUser] = useState(null);
    const [confirmOpen, setConfirmOpen] = useState(false);
    const [userToDelete, setUserToDelete] = useState(null);
    const [searchText, setSearchText] = useState('');
    const [formErrors, setFormErrors] = useState({});

    const defaultFormData = {
        first_name: '',
        last_name: '',
        email: '',
        profile_link_image: '',
    };

    const [formData, setFormData] = useState(defaultFormData);


    const handleAdd = () => {
        setSelectedUser(null);
        setModalMode('create');
        setIsModalOpen(true);
    };

    const handleEdit = user => {
        setSelectedUser(user);
        setModalMode('edit');
        setIsModalOpen(true);
    };

    const handleDelete = user => {
        setUserToDelete(user);
        setConfirmOpen(true);
    };

    const confirmDelete = () => {
        if (userToDelete) deleteUser(userToDelete.id);
        setConfirmOpen(false);
        setUserToDelete(null);
    };

    const handleFormSubmit = async formData => {
        const errors = validateUser(formData);
        setFormErrors(errors); // store errors

        if (Object.keys(errors).length > 0) return; // stop submission

        const isEdit = modalMode === 'edit';
        const id = isEdit ? selectedUser?.id : null;
        await saveUser(formData, isEdit, id);
        setIsModalOpen(false);
        setFormErrors({}); // clear errors
    };


    const filteredUsers = users?.filter(user =>
        `${user.first_name} ${user.last_name}`.toLowerCase().includes(searchText.toLowerCase())
    );

    return (
        <div className="p-4">
                <div className="mb-4">
                    <h2 className="text-2xl font-bold">Users</h2>
                    <div className="flex justify-between items-center space-x-4">
                        <div className="flex space-x-2">
                            <button onClick={() => setView('table')} className={`text-sm ${view === 'table' ? 'text-blue-600 font-semibold border border-blue-400 rounded flex px-2 py-1' : 'text-gray-600 border border-gray-400 rounded flex px-2 py-1'}`}><TableIcon /><span>Table</span></button>
                            <button onClick={() => setView('card')} className={`text-sm ${view === 'card' ? 'text-blue-600 font-semibold border border-blue-400 rounded flex px-2 py-1' : 'text-gray-600 border border-gray-400 rounded flex px-2 py-1'}`}><CardIcon /><span>Card</span></button>
                        </div>
                        <div>
                            <input
                                type="text"
                                placeholder="input search text"
                                value={search}
                                onChange={(e) => setSearch(e.target.value)}
                                className="border px-2 py-1 rounded-md"
                            />
                            <button
                                className="bg-blue-500 text-white px-4 py-1 ml-3 rounded-md"
                                onClick={() => { setShowModal(true); setIsEditMode(false); setFormData({ first_name: '', last_name: '', email: '', profile_link_image: '' }); }}
                            >
                                Create User
                            </button>
                        </div>
                    </div>
                </div>

        {viewType === 'table' ? (
            <UserTable users={filteredUsers} onEdit={handleEdit} onDelete={handleDelete} loading={loading} />
        ) : (
            <UserCardGrid users={filteredUsers} onEdit={handleEdit} onDelete={handleDelete} loading={loading} />
        )}

            <UserModal
                formData={formData}
                setFormData={setFormData}
                isOpen={isModalOpen}
                onClose={() => {
                    setIsModalOpen(false);
                    setFormErrors({});
                    setFormData(defaultFormData); // reset form
                }}
                onSubmit={handleFormSubmit}
                defaultValues={selectedUser} // <-- make sure selectedUser exists when in edit mode
                mode={modalMode} // Pass mode
                errors={formErrors} // Pass errors
            />


            <ConfirmModal
                isOpen={confirmOpen}
                onConfirm={confirmDelete}
                onCancel={() => setConfirmOpen(false)}
                message={`Are you sure you want to delete ${userToDelete?.first_name}?`}
            />
        </div>
    );
};

export default Users;
