import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getusers, removeUser } from '../features/users/userThunks';
import { useNavigate } from 'react-router-dom';

export default function Users() {
    const dispatch = useDispatch();
    const nav = useNavigate()

    // Grab everything you need from the user slice in one selector call
    const { loading, users, error } = useSelector(state => state.userSlice);

    useEffect(() => {
        dispatch(getusers());
    }, [dispatch]);

    useEffect(() => {
        // console.log('Users updated:', users);
    }, [users]);
    const addUser = () => { nav('/create') }
    const editUser = (user) => { nav(`/edit/${user.id}`) }
    const deleteUser = (id) => {
        const res = window.confirm("do you want to delete the user?");
        if (res) {
            dispatch(removeUser(id));
            dispatch(getusers())
        }
    }
    return (
        <div className="flex flex-col  bg-gray-50 shadow-sm p-4 min-h-screen min-w-[100dvw] m-1">
            <div className='flex-row justify-items-end w-full'><button className='border rounded-md p-2 my-1' onClick={addUser}>Add User</button></div>
            {loading && <p className='justify-center place-items-center min-h-screen min-w-[100dvw]'>Loading Users...</p>}


            {!loading && users?.length > 0 && (
                <ul>
                    {users?.map(user => (
                        <li key={user.id}><div className='row flex justify-start space-x-2'>

                            <img src={user.avatar} />
                            <div className='col space-y-2 flex-col'>
                                {user.email}{user.first_name}
                                {user.last_name}
                            </div>
                            <div><button onClick={() => editUser(user)}>Edit</button><br />
                                <button onClick={()=>{
                                    deleteUser(user.id)}}>Delete</button>
                            </div>
                        </div></li>
                    ))}
                </ul>
            )}

            {error && <p className="text-red-500">{error}</p>}

            {!loading && users.length === 0 && !error && <p>No users found.</p>}
        </div>
    );
}
