// Updated Users.jsx with popup modal for Create User
import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getusers, removeUser, createUser } from '../features/users/userThunks';

const CardIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none">
        <rect x="3" y="3" width="7" height="7" fill="currentColor" />
        <rect x="14" y="3" width="7" height="7" fill="currentColor" />
        <rect x="14" y="14" width="7" height="7" fill="currentColor" />
        <rect x="3" y="14" width="7" height="7" fill="currentColor" />
    </svg>
);

const TableIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none">
        <rect x="3" y="4" width="18" height="2" fill="currentColor" />
        <rect x="3" y="10" width="18" height="2" fill="currentColor" />
        <rect x="3" y="16" width="18" height="2" fill="currentColor" />
    </svg>
);

export default function Users() {
    const dispatch = useDispatch();
    const { loading, users, error } = useSelector(state => state.userSlice);
    const [search, setSearch] = useState('');
    const [page, setPage] = useState(1);
    const [view, setView] = useState('table');
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({ username: '', password: '', email: '' });
    const perPage = 5;

    useEffect(() => { dispatch(getusers()); }, [dispatch]);

    const filteredUsers = users?.filter(user =>
        `${user.first_name} ${user.last_name} ${user.email}`.toLowerCase().includes(search.toLowerCase())
    );

    const totalPages = Math.ceil(filteredUsers.length / perPage);
    const currentUsers = filteredUsers.slice((page - 1) * perPage, page * perPage);

    const handleCreate = (e) => {
        e.preventDefault();

        const { username, email, password } = formData;
        if (!username || !email || !password) {
            alert("Please fill in all fields.");
            return;
        }

        dispatch(createUser({ username, email, password }))
            .then(() => {
                setShowModal(false, userId);
                setFormData({ username: '', password: '', email: '' });
                dispatch(getusers()); // refresh user list
            });
    };

    const handleDelete = id => {
        if (window.confirm("Do you want to delete the user?")) {
            dispatch(removeUser(id));
        }
    };

    return (
        <div className='min-h-screen min-w-full bg-gray-100 p-6'>
            <div className="rounded-md shadow-lg bg-white p-4 mt-10 mx-10">
                <div className="mb-4">
                    <h2 className="text-2xl font-bold">Users</h2>
                    <div className="flex justify-between items-center space-x-4">
                        <div className="flex space-x-2">
                            <button onClick={() => setView('table')} className={`text-sm ${view === 'table' ? 'text-blue-600 font-semibold border border-blue-400 rounded flex px-2 py-1' : 'text-gray-600 border border-gray-400 rounded flex px-2 py-1'}`}><TableIcon /><span>Table</span></button>
                            <button onClick={() => setView('card')} className={`text-sm ${view === 'card' ? 'text-blue-600 font-semibold border border-blue-400 rounded flex px-2 py-1' : 'text-gray-600 border border-gray-400 rounded flex px-2 py-1'}`}><CardIcon /><span>Card</span></button>
                        </div>
                        <div>
                            <input
                                type="text"
                                placeholder="input search text"
                                value={search}
                                onChange={(e) => setSearch(e.target.value)}
                                className="border px-2 py-1 rounded-md"
                            />
                            <button
                                className="bg-blue-500 text-white px-4 py-1 ml-3 rounded-md"
                                onClick={() => setShowModal(true, null)}
                            >
                                Create User
                            </button>
                        </div>
                    </div>
                </div>

                {loading && <p>Loading Users...</p>}

                {!loading && currentUsers?.length > 0 && view === 'table' && (
                    <table className="min-w-full bg-white shadow-md rounded-lg overflow-hidden">
                        <thead>
                            <tr className="bg-gray-100 text-left text-sm font-semibold text-gray-700">
                                <th className="p-3">Email</th>
                                <th className="p-3">First Name</th>
                                <th className="p-3">Last Name</th>
                                <th className="p-3">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {currentUsers.map(user => (
                                <tr key={user.id} className="border-b hover:bg-gray-50">
                                    <td className="p-3 flex items-center space-x-2">
                                        <img src={user.avatar} alt={user.email} className="w-8 h-8 rounded-full" />
                                        <a href={`mailto:${user.email}`} className="text-blue-600 hover:underline">{user.email}</a>
                                    </td>
                                    <td className="p-3">{user.first_name}</td>
                                    <td className="p-3">{user.last_name}</td>
                                    <td className="p-3 space-x-2">
                                        <button onClick={() => setShowModal(true, user.id)} className="bg-blue-500 text-white px-3 py-1 rounded">Edit</button>
                                        <button onClick={() => handleDelete(user.id)} className="bg-red-500 text-white px-3 py-1 rounded">Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}

                {!loading && currentUsers?.length > 0 && view === 'card' && (
                    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                        {currentUsers.map(user => (
                            <div key={user.id} className="relative p-4 bg-white shadow-md rounded-lg hover:bg-gray-100 transition-all">
                                <img src={user.avatar} alt={user.email} className="w-20 h-20 rounded-full mx-auto" />
                                <h3 className="text-center font-bold text-lg mt-2">{user.first_name} {user.last_name}</h3>
                                <p className="text-center text-gray-600">{user.email}</p>
                                <div className="absolute bottom-3 left-0 right-0 flex justify-center gap-4 opacity-0 hover:opacity-100 transition-opacity">
                                    <button className="bg-purple-500 text-white p-2 rounded-full hover:bg-purple-600">‚úèÔ∏è</button>
                                    <button onClick={() => handleDelete(user.id)} className="bg-red-500 text-white p-2 rounded-full hover:bg-red-600">üóëÔ∏è</button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}

                {error && <p className="text-red-500 mt-4">{error}</p>}
                {!loading && filteredUsers.length === 0 && !error && <p className="mt-4">No users found.</p>}

                <div className="flex justify-end items-center space-x-2 mt-6">
                    <button onClick={() => setPage(prev => Math.max(prev - 1, 1))} disabled={page === 1} className="px-3 py-1 border rounded disabled:opacity-50">&lt;</button>
                    {Array.from({ length: totalPages }, (_, i) => (
                        <button key={i + 1} onClick={() => setPage(i + 1)} className={`px-3 py-1 border rounded ${page === i + 1 ? 'bg-blue-500 text-white' : ''}`}>{i + 1}</button>
                    ))}
                    <button onClick={() => setPage(prev => Math.min(prev + 1, totalPages))} disabled={page === totalPages} className="px-3 py-1 border rounded disabled:opacity-50">&gt;</button>
                </div>
            </div>

            {/* Modal */}
            {showModal && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                    <div className="bg-white p-6 rounded shadow-lg w-[90%] sm:w-[500px]">
                        <div className="flex justify-between items-center mb-4">
                            <h3 className="text-xl font-semibold">Create New User</h3>
                            <button onClick={() => setShowModal(false, null)} className="text-gray-600 text-xl">&times;</button>
                        </div>
                        <div className="space-y-4">
                            <input type="text" name="username" value={formData.username} onChange={e => setFormData({ ...formData, username: e.target.value })} placeholder="Username" className="w-full border p-2 rounded" />
                            <input type="email" name="email" value={formData.email} onChange={e => setFormData({ ...formData, email: e.target.value })} placeholder="Email" className="w-full border p-2 rounded" />
                            <input type="password" name="password" value={formData.password} onChange={e => setFormData({ ...formData, password: e.target.value })} placeholder="Password" className="w-full border p-2 rounded" />
                        </div>
                        <div className="flex justify-end mt-6 space-x-2">
                            <button onClick={() => setShowModal(false, null)} className="border px-4 py-2 rounded">Cancel</button>
                            <button onClick={handleCreate} className="bg-blue-500 text-white px-4 py-2 rounded">Submit</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
