import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getusers, removeUser } from '../features/users/userThunks';
import UserModal from '../components/UserModal';
import ConfirmModal from '../components/ConfirmModal';
import UserCardGrid from '../components/UserCardGrid';
import UserTable from '../components/UserTable';
import { toast } from 'react-toastify';

const Users = () => {
    const dispatch = useDispatch();
    const { users, loading } = useSelector((state) => state.userSlice);

    const [view, setView] = useState('table');
    const [showModal, setShowModal] = useState(false);
    const [confirmDelete, setConfirmDelete] = useState({ show: false, userId: null });
    const [searchTerm, setSearchTerm] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const [formData, setFormData] = useState({
        first_name: '',
        last_name: '',
        email: '',
        profile_link_image: '',
    });
    const [isEditMode, setIsEditMode] = useState(false);
    const [editingUserId, setEditingUserId] = useState(null);
    const [validationErrors, setValidationErrors] = useState({});
    const perPage = 6;

    useEffect(() => {
        dispatch(getusers());
    }, [dispatch]);

    const filteredUsers = users?.filter((user) =>
        `${user.first_name} ${user.last_name} ${user.email}`.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const totalPages = Math.ceil(filteredUsers.length / perPage);
    const currentUsers = filteredUsers.slice((currentPage - 1) * perPage, currentPage * perPage);

    const handleAddUser = () => {
        setIsEditMode(false);
        setFormData({ first_name: '', last_name: '', email: '', profile_link_image: '' });
        setShowModal(true);
    };

    const handleEdit = (user) => {
        setIsEditMode(true);
        setEditingUserId(user.id);
        setFormData({
            first_name: user.first_name,
            last_name: user.last_name,
            email: user.email,
            profile_link_image: user.avatar || '',
        });
        setShowModal(true);
    };

    const handleDelete = (userId) => {
        setConfirmDelete({ show: true, userId });
    };

    const handleViewToggle = () => {
        setView(view === 'card' ? 'table' : 'card');
    };

    return (
        <div className="min-h-screen bg-gray-100 px-6 py-10">
            <div className="bg-white p-6 rounded-lg shadow-md">
                <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4 mb-6">
                    <div className="flex gap-3 w-full sm:w-auto">
                        <button
                            onClick={handleViewToggle}
                            className="inline-flex items-center justify-center px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-100"
                        >
                            {view === 'card' ? 'Table View' : 'Card View'}
                        </button>
                        <button
                            onClick={handleAddUser}
                            className="inline-flex items-center justify-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-md text-sm font-medium"
                        >
                            + Add User
                        </button>
                    </div>
                    <div className="w-full sm:w-64">
                        <input
                            type="text"
                            placeholder="Search users..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="w-full border border-gray-300 px-4 py-2 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                        />
                    </div>
                </div>

                {loading && <p>Loading users...</p>}

                {!loading && filteredUsers.length > 0 && view === 'table' && (
                    <UserTable users={currentUsers} onEdit={handleEdit} onDelete={handleDelete} />
                )}

                {!loading && filteredUsers.length > 0 && view === 'card' && (
                    <UserCardGrid users={currentUsers} onEdit={handleEdit} onDelete={handleDelete} />
                )}

                {!loading && filteredUsers.length === 0 && <p>No users found.</p>}

                <div className="flex justify-end mt-6 gap-2">
                    <button
                        onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                        disabled={currentPage === 1}
                        className="px-3 py-1 border rounded disabled:opacity-50"
                    >
                        &lt;
                    </button>
                    {Array.from({ length: totalPages }, (_, i) => (
                        <button
                            key={i + 1}
                            onClick={() => setCurrentPage(i + 1)}
                            className={`px-3 py-1 border rounded ${currentPage === i + 1 ? 'bg-blue-500 text-white' : ''}`}
                        >
                            {i + 1}
                        </button>
                    ))}
                    <button
                        onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
                        disabled={currentPage === totalPages}
                        className="px-3 py-1 border rounded disabled:opacity-50"
                    >
                        &gt;
                    </button>
                </div>
            </div>

            <UserModal
                formData={formData}
                setFormData={setFormData}
                isOpen={isModalOpen}
                onClose={() => {
                    setIsModalOpen(false);
                    setFormErrors({});
                    setFormData(defaultFormData); // reset form
                }}
                onSubmit={handleFormSubmit}
                defaultValues={selectedUser} // <-- make sure selectedUser exists when in edit mode
                mode={modalMode} // Pass mode
                errors={formErrors} // Pass errors
            />


            <ConfirmModal
                isOpen={confirmOpen}
                onConfirm={confirmDelete}
                onCancel={() => setConfirmOpen(false)}
                message={`Are you sure you want to delete ${userToDelete?.first_name}?`}
            />
        </div>
    );
};

export default Users;
