import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';

UserModal.propTypes = {
    isOpen: PropTypes.bool.isRequired,
    onClose: PropTypes.func.isRequired,
    onSubmit: PropTypes.func.isRequired,
    defaultValues: PropTypes.object,
    mode: PropTypes.oneOf(['create', 'edit']).isRequired,
};

const UserModal = ({ isOpen, onClose, onSubmit, defaultValues = {}, mode = 'create' }) => {
    const [formData, setFormData] = useState({ first_name: '', last_name: '', email: '', avatar: '' });
    const [errors, setErrors] = useState({});

    useEffect(() => {
        if (defaultValues) setFormData(defaultValues);
    }, [defaultValues]);

    const handleChange = e => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async e => {
        e.preventDefault();
        const validationErrors = await onSubmit(formData);
        setErrors(validationErrors || {});
    };

    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex justify-center items-center z-50">
            <div className="bg-white rounded p-6 w-full max-w-md">
                <h2 className="text-xl mb-4">{mode === 'edit' ? 'Edit User' : 'Add User'}</h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <input
                        type="text"
                        name="first_name"
                        placeholder="First Name"
                        value={formData.first_name}
                        onChange={handleChange}
                        className="w-full border px-3 py-2 rounded"
                    />
                    {errors.first_name && <p className="text-red-600 text-sm">{errors.first_name}</p>}

                    <input
                        type="text"
                        name="last_name"
                        placeholder="Last Name"
                        value={formData.last_name}
                        onChange={handleChange}
                        className="w-full border px-3 py-2 rounded"
                    />
                    {errors.last_name && <p className="text-red-600 text-sm">{errors.last_name}</p>}

                    <input
                        type="email"
                        name="email"
                        placeholder="Email"
                        value={formData.email}
                        onChange={handleChange}
                        className="w-full border px-3 py-2 rounded"
                    />
                    {errors.email && <p className="text-red-600 text-sm">{errors.email}</p>}

                    <input
                        type="text"
                        name="avatar"
                        placeholder="Avatar URL (optional)"
                        value={formData.avatar}
                        onChange={handleChange}
                        className="w-full border px-3 py-2 rounded"
                    />

                    <div className="flex justify-end space-x-2">
                        <button type="button" onClick={onClose} className="px-4 py-2 bg-gray-300 rounded">Cancel</button>
                        <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded">
                            {mode === 'edit' ? 'Update' : 'Create'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default UserModal;
