export const validateUser = ({ first_name, last_name, email, profile_link_image }) => {
    const errors = {};
    if (!first_name.trim()) errors.first_name = "First name is required";
    if (!last_name.trim()) errors.last_name = "Last name is required";
    if (!email.trim()) errors.email = "Email is required";
    else if (!/^\S+@\S+\.\S+$/.test(email)) errors.email = "Email is invalid";
    if (!profile_link_image.trim()) errors.profile_link_image = "Profile image link is required";
    return errors;
};
