import React from 'react';
import PropTypes from 'prop-types';

UserCardGrid.propTypes = {
    users: PropTypes.arrayOf(PropTypes.object).isRequired,
    onEdit: PropTypes.func.isRequired,
    onDelete: PropTypes.func.isRequired,
    loading: PropTypes.bool.isRequired,
};

const UserCardGrid = ({ users, onEdit, onDelete, loading }) => {
    if (loading) return <p>Loading users...</p>;

    if (users?.length === 0) {
        return <p className="text-gray-500 text-center mt-4">No users found</p>;
    }

    return (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {users.map(user => (
                <div key={user.id} className="bg-white border shadow rounded p-4">
                    <h3 className="text-lg font-semibold">{user.first_name} {user.last_name}</h3>
                    <p className="text-gray-600">{user.email}</p>
                    <div className="mt-3 flex space-x-2">
                        <button onClick={() => onEdit(user)} className="px-3 py-1 bg-yellow-500 text-white rounded">Edit</button>
                        <button onClick={() => onDelete(user)} className="px-3 py-1 bg-red-600 text-white rounded">Delete</button>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default UserCardGrid;
