import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getusers, removeUser } from '../features/users/userThunks';
import { useNavigate } from 'react-router-dom';
export default function Users() {
  const dispatch = useDispatch();
  const nav = useNavigate();

  const { loading, users, error } = useSelector(state => state.userSlice);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);
  const [view, setView] = useState('table');
  const [showModal, setShowModal] = useState(false);
  const perPage = 5;

  useEffect(() => {
    dispatch(getusers());
  }, [dispatch]);

  const editUser = user => nav(`/edit/${user.id}`);

  const deleteUser = id => {
    const res = window.confirm("Do you want to delete the user?");
    if (res) {
      dispatch(removeUser(id));
      dispatch(getusers());
    }
  };

  const handleCreateUser = (newUser) => {
    setShowModal(false);
    // Dispatch or handle user creation logic here
    console.log("New User:", newUser);
  };

  const filteredUsers = users?.filter(user =>
    `${user.first_name} ${user.last_name} ${user.email}`.toLowerCase().includes(search.toLowerCase())
  );

  const totalPages = Math.ceil(filteredUsers.length / perPage);
  const currentUsers = filteredUsers.slice((page - 1) * perPage, page * perPage);

  return (
    <div className="min-h-screen min-w-full bg-gray-100 p-6">
      {showModal && <CreateUserModal onClose={() => setShowModal(false)} onSubmit={handleCreateUser} />}

      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold">Users</h2>
        <div className="flex items-center space-x-4">
          <div className="flex space-x-2 border px-2 py-1 rounded-md">
            <button onClick={() => setView('table')} className={`text-sm ${view === 'table' ? 'text-blue-600 font-semibold' : ''}`}><TableIcon /></button>
            <button onClick={() => setView('card')} className={`text-sm ${view === 'card' ? 'text-blue-600 font-semibold' : ''}`}><CardIcon /></button>
          </div>
          <input
            type="text"
            placeholder="input search text"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="border px-2 py-1 rounded-md"
          />
          <button
            className="bg-blue-500 text-white px-4 py-1 rounded-md"
            onClick={() => setShowModal(true)}
          >
            Create User
          </button>
        </div>
      </div>

      {loading && <p>Loading Users...</p>}

      {!loading && currentUsers?.length > 0 && view === 'table' && (
        <table className="min-w-full bg-white shadow-md rounded-lg overflow-hidden">
          <thead>
            <tr className="bg-gray-100 text-left text-sm font-semibold text-gray-700">
              <th className="p-3">Email</th>
              <th className="p-3">First Name</th>
              <th className="p-3">Last Name</th>
              <th className="p-3">Action</th>
            </tr>
          </thead>
          <tbody>
            {currentUsers.map(user => (
              <tr key={user.id} className="border-b hover:bg-gray-50">
                <td className="p-3 flex items-center space-x-2">
                  <img src={user.avatar} alt={user.email} className="w-8 h-8 rounded-full" />
                  <a href={`mailto:${user.email}`} className="text-blue-600 hover:underline">{user.email}</a>
                </td>
                <td className="p-3">{user.first_name}</td>
                <td className="p-3">{user.last_name}</td>
                <td className="p-3 space-x-2">
                  <button
                    onClick={() => editUser(user)}
                    className="bg-blue-500 text-white px-3 py-1 rounded"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => deleteUser(user.id)}
                    className="bg-red-500 text-white px-3 py-1 rounded"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {!loading && currentUsers?.length > 0 && view === 'card' && (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {currentUsers.map(user => (
            <div key={user.id} className="relative p-4 bg-white shadow-md rounded-lg hover:bg-gray-100 transition-all">
              <img src={user.avatar} alt={user.email} className="w-20 h-20 rounded-full mx-auto" />
              <h3 className="text-center font-bold text-lg mt-2">{user.first_name} {user.last_name}</h3>
              <p className="text-center text-gray-600">{user.email}</p>
              <div className="absolute bottom-3 left-0 right-0 flex justify-center gap-4 opacity-0 hover:opacity-100 transition-opacity">
                <button onClick={() => editUser(user)} className="bg-purple-500 text-white p-2 rounded-full hover:bg-purple-600">
                  ✏️
                </button>
                <button onClick={() => deleteUser(user.id)} className="bg-red-500 text-white p-2 rounded-full hover:bg-red-600">
                  🗑️
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {error && <p className="text-red-500 mt-4">{error}</p>}
      {!loading && filteredUsers.length === 0 && !error && <p className="mt-4">No users found.</p>}

      <div className="flex justify-center items-center space-x-2 mt-6">
        <button
          onClick={() => setPage(prev => Math.max(prev - 1, 1))}
          disabled={page === 1}
          className="px-3 py-1 border rounded disabled:opacity-50"
        >
          &lt;
        </button>
        {Array.from({ length: totalPages }, (_, i) => (
          <button
            key={i + 1}
            onClick={() => setPage(i + 1)}
            className={`px-3 py-1 border rounded ${page === i + 1 ? 'bg-blue-500 text-white' : ''}`}
          >
            {i + 1}
          </button>
        ))}
        <button
          onClick={() => setPage(prev => Math.min(prev + 1, totalPages))}
          disabled={page === totalPages}
          className="px-3 py-1 border rounded disabled:opacity-50"
        >
          &gt;
        </button>
      </div>
    </div>
  );
}
